String content() {
  return "import 'dart:convert'; \nimport 'package:flutter/material.dart'; \nimport 'package:flutter/services.dart'; \nimport 'package:get/get.dart'; \nimport '../model/language_model.dart'; \nclass LocalizationController extends GetxController implements GetxService { \nMap<String, Map<String, String>> translations = {}; \nLocale get locale => _locale; \nfinal _supportedLanguageList = <LanguageModel>[ \nLanguageModel( \nimageUrl: 'ðŸ‡ºðŸ‡¸', \nlanguageName: 'English', \nlanguageCode: 'en', \ncountryCode: 'US', \n), \n]; \nfinal Locale _locale = const Locale( \n'en', \n'US', \n); \nFuture<void> init() async { \nawait _loadLanguages(); \n} \n_loadLanguages() async { \nfor (LanguageModel languageModel in _supportedLanguageList) { \nString jsonStringValues = await rootBundle \n.loadString('asset/locale/\${languageModel.languageCode}.json'); \nMap<String, dynamic> mappedJson = jsonDecode(jsonStringValues); \nMap<String, String> json = {}; \nmappedJson.forEach((key, value) { \njson[key] = value.toString(); \n}); \ntranslations[ \n'\${languageModel.languageCode}_\${languageModel.countryCode}'] = json; \n} \n} \n}";
}
